"use client";

import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import Divider from "@mui/material/Divider";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";

export default function PaymentOverviewCard() {
  return (
    <Card
      sx={(theme) => ({
        p: 3,
        display: "flex",
        alignItems: "stretch",
        gap: 3,
        borderRadius: 2,
        boxShadow: 1,
      })}
    >
      <Box
        component="section"
        aria-label="Total Outstanding"
        sx={{ flex: 1, display: "flex", gap: 2 }}
      >
        <Box
          aria-hidden="true"
          sx={(theme) => ({
            display: "flex",
            gap: 0.5,
            alignItems: "flex-start",
            height: 20,
          })}
        >
          <Box
            sx={(theme) => ({
              width: 4,
              height: 16,
              bgcolor: (theme.vars || theme).palette.primary.main,
              opacity: 0.3,
              borderRadius: 0.5,
            })}
          />
          <Box
            sx={(theme) => ({
              width: 4,
              height: 20,
              bgcolor: (theme.vars || theme).palette.primary.main,
              opacity: 0.6,
              borderRadius: 0.5,
            })}
          />
        </Box>
        <Stack spacing={0.5}>
          <Typography
            variant="body2"
            sx={(theme) => ({
              color: (theme.vars || theme).palette.text.secondary,
            })}
          >
            Total Outstanding
          </Typography>
          <Typography
            variant="h5"
            component="div"
            sx={(theme) => ({
              fontWeight: 600,
              color: (theme.vars || theme).palette.text.primary,
            })}
          >
            $58,764.25
          </Typography>
        </Stack>
      </Box>

      <Divider orientation="vertical" flexItem />

      <Box
        component="section"
        aria-label="Upcoming Payment"
        sx={{ flex: 1, display: "flex", gap: 2 }}
      >
        <Box
          aria-hidden="true"
          sx={(theme) => ({
            display: "flex",
            gap: 0.5,
            alignItems: "flex-start",
            height: 20,
          })}
        >
          <Box
            sx={(theme) => ({
              width: 8,
              height: 8,
              bgcolor: (theme.vars || theme).palette.primary.main,
              opacity: 0.3,
              borderRadius: 0.5,
              alignSelf: "center",
            })}
          />
          <Box
            sx={(theme) => ({
              width: 8,
              height: 12,
              bgcolor: (theme.vars || theme).palette.primary.main,
              opacity: 0.6,
              borderRadius: 0.5,
              alignSelf: "flex-end",
            })}
          />
        </Box>
        <Stack spacing={0.5}>
          <Typography
            variant="body2"
            sx={(theme) => ({
              color: (theme.vars || theme).palette.text.secondary,
            })}
          >
            Upcoming Payment
          </Typography>
          <Typography
            variant="h5"
            component="div"
            sx={(theme) => ({
              fontWeight: 600,
              color: (theme.vars || theme).palette.text.primary,
            })}
          >
            April 1st, 2022
          </Typography>
        </Stack>
      </Box>
    </Card>
  );
}
