"use client";

import * as React from "react";
import {
  AppBar,
  Box,
  Button,
  Container,
  IconButton,
  Link,
  Toolbar,
  Typography,
  useScrollTrigger,
} from "@mui/material";
import { HelpOutline } from "@mui/icons-material";

interface NavItem {
  label: string;
  href: string;
}

const navItems: NavItem[] = [
  { label: "Home", href: "#home" },
  { label: "Services", href: "#services" },
  { label: "Pricing", href: "#pricing" },
  { label: "Features", href: "#features" },
];

export function SiteHeader01() {
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return (
    <AppBar
      position="sticky"
      elevation={trigger ? 1 : 0}
      sx={{
        bgcolor: "background.paper",
        borderBottom: 1,
        borderColor: "divider",
      }}
    >
      <Container maxWidth="xl">
        <Toolbar
          sx={{
            minHeight: { xs: 64, md: 72 },
            px: { xs: 0 },
          }}
        >
          {/* Logo */}
          <Link
            href="/"
            underline="none"
            sx={{
              display: "flex",
              alignItems: "center",
              gap: 1,
              color: "text.primary",
              mr: { xs: "auto", md: 4 },
            }}
            aria-label="N Banking home"
          >
            <Box
              component="span"
              sx={{
                width: 32,
                height: 32,
                bgcolor: "text.primary",
                borderRadius: 1,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontWeight: "bold",
                fontSize: "1.25rem",
                color: "background.paper",
              }}
            >
              N
            </Box>
            <Typography
              variant="h6"
              component="span"
              fontWeight="600"
              sx={{
                display: { xs: "none", sm: "inline" },
              }}
            >
              Banking
            </Typography>
          </Link>

          {/* Navigation */}
          <Box
            component="nav"
            aria-label="Main navigation"
            sx={{
              display: { xs: "none", md: "flex" },
              gap: 3,
              mx: "auto",
            }}
          >
            {navItems.map((item) => (
              <Link
                key={item.label}
                href={item.href}
                underline="none"
                sx={{
                  color: "text.primary",
                  fontSize: "0.9375rem",
                  fontWeight: 500,
                  transition: "color 0.2s",
                  "&:hover": {
                    color: "primary.main",
                  },
                  "&:focus-visible": {
                    outline: "2px solid",
                    outlineColor: "primary.main",
                    outlineOffset: 2,
                    borderRadius: 0.5,
                  },
                }}
              >
                {item.label}
              </Link>
            ))}
          </Box>

          {/* CTA Button */}
          <Button
            variant="contained"
            sx={{
              ml: { xs: 0, md: "auto" },
              mr: 2,
              borderRadius: 8,
              px: 3,
              py: 1,
              textTransform: "none",
              fontWeight: 600,
              bgcolor: "#DFFF00",
              color: "text.primary",
              "&:hover": {
                bgcolor: "#c8e600",
              },
              "&:focus-visible": {
                outline: "2px solid",
                outlineColor: "primary.main",
                outlineOffset: 2,
              },
            }}
            aria-label="Open a new account"
          >
            Open Account
          </Button>

          {/* Support Button */}
          <IconButton
            size="large"
            sx={{
              bgcolor: "text.primary",
              color: "background.paper",
              "&:hover": {
                bgcolor: "text.secondary",
              },
              "&:focus-visible": {
                outline: "2px solid",
                outlineColor: "primary.main",
                outlineOffset: 2,
              },
            }}
            aria-label="Get help and support"
          >
            <HelpOutline />
          </IconButton>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
