"use client";

import * as React from "react";
import Box from "@mui/material/Box";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Link from "@mui/material/Link";
import Typography from "@mui/material/Typography";
import AutoAwesomeIcon from "@mui/icons-material/AutoAwesome";
import OpenInNewIcon from "@mui/icons-material/OpenInNew";

export default function Tabs01() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event: React.SyntheticEvent, newValue: number) => {
    setValue(newValue);
  };

  return (
    <Box
      sx={(theme) => ({
        bgcolor: "grey.50",
        borderRadius: 2,
        p: 2,
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        gap: 2,
        ...theme.applyStyles("dark", {
          bgcolor: "grey.900",
        }),
      })}
    >
      <Tabs
        value={value}
        onChange={handleChange}
        sx={{
          minHeight: 36,
          "& .MuiTabs-indicator": {
            display: "none",
          },
        }}
      >
        <Tab
          label="Install with AI"
          icon={<AutoAwesomeIcon />}
          iconPosition="start"
          sx={(theme) => ({
            minHeight: 36,
            borderRadius: 99,
            textTransform: "none",
            px: 2.5,
            py: 1,
            fontSize: "0.875rem",
            fontWeight: 500,
            bgcolor: value === 0 ? "text.primary" : "transparent",
            color: value === 0 ? "background.paper" : "text.primary",
            transition: "all 0.2s",
            "&:hover": {
              bgcolor: value === 0 ? "text.primary" : "action.hover",
            },
            "& .MuiTab-iconWrapper": {
              mr: 0.75,
              fontSize: "1.125rem",
            },
            ...theme.applyStyles("dark", {
              bgcolor: value === 0 ? "grey.100" : "transparent",
              color: value === 0 ? "grey.900" : "text.primary",
              "&:hover": {
                bgcolor: value === 0 ? "grey.100" : "action.hover",
              },
            }),
          })}
        />
        <Tab
          label="Web signup"
          sx={(theme) => ({
            minHeight: 36,
            borderRadius: 99,
            textTransform: "none",
            px: 2.5,
            py: 1,
            fontSize: "0.875rem",
            fontWeight: 500,
            bgcolor: value === 1 ? "text.primary" : "transparent",
            color: value === 1 ? "background.paper" : "text.primary",
            transition: "all 0.2s",
            "&:hover": {
              bgcolor: value === 1 ? "text.primary" : "action.hover",
            },
            ...theme.applyStyles("dark", {
              bgcolor: value === 1 ? "grey.100" : "transparent",
              color: value === 1 ? "grey.900" : "text.primary",
              "&:hover": {
                bgcolor: value === 1 ? "grey.100" : "action.hover",
              },
            }),
          })}
        />
      </Tabs>

      <Box sx={{ display: "flex", alignItems: "center", gap: 0.5 }}>
        <Typography variant="body2" color="text.secondary">
          Need help?
        </Typography>
        <Link
          href="#"
          underline="always"
          variant="body2"
          sx={{
            display: "inline-flex",
            alignItems: "center",
            gap: 0.25,
            color: "text.primary",
            fontWeight: 500,
          }}
          rel="noopener noreferrer"
          target="_blank"
          aria-label="Talk to a human (opens in new tab)"
        >
          Talk to a human
          <OpenInNewIcon sx={{ fontSize: "0.875rem" }} />
        </Link>
      </Box>
    </Box>
  );
}
